

export function init(grid){
    for(let i = 0; i < 3; i++){
        for(let j = 0; j < 8; j++){
            if((i + j) % 2 === 1){
                grid[i][j] = 'black';
            }
        }
    }

    for(let i = 5; i < 8; i++){
        for(let j = 0; j < 8; j++){
            if((i + j) % 2 === 1){
                grid[i][j] = 'white';
            }
        }
    }
}

export function addPiece(grid, x, y, color){
    grid[x][y] = color;
}

export function removePiece(grid, x, y){
    grid[x][y] = null;
}

export function gridToString(grid){
    let str = '';
    for(let i = 0; i < 8; i++){
        for(let j = 0; j < 8; j++){
            str += grid[i][j] === null ? '0' : grid[i][j] === 'black' ? '1' : '2';
        }
    }
    return str;
}


export function resetGrid(grid){
    for(let i = 0; i < 8; i++){
        for(let j = 0; j < 8; j++){
            grid[i][j] = null;
        }
    }
}