const grid = Array(8).fill(null).map(() => Array(8).fill(null));

function init(){
    for(let i = 0; i < 3; i++){
        for(let j = 0; j < 8; j++){
            if((i + j) % 2 === 1){
                grid[i][j] = 'black';
            }
        }
    }

    for(let i = 5; i < 8; i++){
        for(let j = 0; j < 8; j++){
            if((i + j) % 2 === 1){
                grid[i][j] = 'white';
            }
        }
    }
}

function addPiece(x, y, color){
    grid[x][y] = color;
}

function removePiece(x, y){
    grid[x][y] = null;
}

function getGrid(){
    return grid;
}

function gridToString(){
    let str = '';
    for(let i = 0; i < 8; i++){
        for(let j = 0; j < 8; j++){
            str += grid[i][j] === null ? '0' : grid[i][j] === 'black' ? '1' : '2';
        }
    }
    return str;
}
